<?phpheader('Content-Type: application/json; charset=utf-8');$uid = $_POST['uid'];$pwd = $_POST['pwd'];$idCult = $_POST['idCult'];$sql = 'CALL "DBA"."consultCultura_Investigador"("idCult" =' . $idCult. ');';$db = sasql_connect("DatabaseName = PROJETOSID ; DBF = C:\Users\Mike\Desktop\Productivity\SIDA\Projeto\PROJETOSID.db ; uid = " . $uid . " ; pwd = " . $pwd);$arr = array();if(!$db){	echo "Connection failed!\n";	die('Não foi possível ligar à base de dados [' . $db->connect_error . ']');}$result = sasql_query($db,$sql);$col_name = sasql_fetch_assoc($result);array_push($arr,$col_name);$result = json_encode($arr);echo $result;?>